vec3 random3( vec3 p ) {
    return fract(sin(vec3(dot(p,vec3(127.1,311.7,124.6)),dot(p,vec3(269.5,183.3,188.8)),dot(p,vec3(244.5,144.3,163.8))))*43758.5453);
}
float lrpn(vec3 p,float n){
    return pow(length(random3(p))/1.732,n);
}


float voro(vec3 C ){
    vec3 st =C;
    float v=0.;

    // Scale
    st *= 3.;

    // Tile the space
    vec3 i_st = floor(st);
    vec3 f_st = fract(st);

    float m_dist = 1.;  // minimum distance
for (int z= -1; z <= 1; z++) {
    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            // Neighbor place in the grid
            vec3 neighbor = vec3(float(x),float(y),float(z));

            // Random position from current + neighbor place in the grid
            vec3 point = random3(i_st + neighbor);

			// Animate the point
            point = 0.5 + 0.5*sin(iTime + 6.2831*point);

			// Vector between the pixel and the point
            vec3 diff = neighbor + point - f_st;

            // Distance to the point
            float dist = length(diff);

            // Keep the closer distance
            m_dist = min(m_dist, dist);
        }
    }
}
    // Draw the min distance (distance field)
    v += m_dist;


    // Show isolines
    // color -= step(.7,abs(sin(27.0*m_dist)))*.5;

    return min(v,1.);
}
const float pi = 3.141592;
const float tau = 2.0*pi;



//#define TIME (3.0*tau/4.0)
#define TIME iTime/2.0
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
const float sphereRadius = 15.0;
const float camRadius = 2.0*sphereRadius;

struct rtpm {
    bool dh;
    float col;
    float br;
    vec3 rd;
    vec3 p;
    int sp;

};
   bool cube(vec3 p, float sz){
    return abs(p.x)<sz&&abs(p.y)<sz&&abs(p.z)<sz;
}

rtpm cube1(rtpm a) 
{     
    rtpm b=a;
    if (!cube(a.p,10.)) {
        b.dh=false;
        return b;
    }
    b.dh=true;
    float v=voro(a.p/10.);
    if(v<lrpn(a.p*iTime,4.)){b.rd.z=-b.rd.z;}
    if(a.sp==0){b.col=(v+b.col)/2.;b.br+=v;b.rd.xy*=1.01;}
    if(a.sp==1){b.col=(v+b.col)/2.;b.br+=v*0.7;b.rd.xy*=1.02;}
    if(a.sp==2){b.col=(v+b.col)/2.;b.br+=v*0.5;b.rd.xy*=1.04;}
    return b;

}

rtpm intersect(vec3 ro, vec3 rd,int sp) {

    vec3 step = normalize(rd)/10.;
    bool f=false;

    rtpm a=rtpm(false,0.,0.,rd,ro,sp);

    const int maxTrace = 1000;
    for (int i = 0; i < maxTrace; i++) {
        if(a.br>100.)break;
        rtpm h = cube1(a);
        if (h.dh) {
           a=h;
           rd=a.rd;
           step = normalize(rd)/10.;
           a.p+=step*0.1;
           i=0;
           f=true;
        }
        else a.p+=step*5.;

    }
    a.dh=f;
 	return a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy - 0.5;
    vec3 worldUp = vec3(0,1,0);
    vec3 camPos = vec3(camRadius*sin(TIME*1.0), 10, 1.0*camRadius*cos(TIME*1.0));
    vec3 lookAt = vec3(0,0,0);
    vec3 camDir = normalize(lookAt - camPos);
    vec3 camRight = normalize(cross(camDir, worldUp));
    vec3 camUp = cross(camRight, camDir);

    vec3 filmCentre = camPos + camDir*0.3;
    vec2 filmSize = vec2(1,iResolution.y / iResolution.x);

    vec3 filmPos = filmCentre + uv.x*filmSize.x*camRight + uv.y*filmSize.y*camUp;
    vec3 ro = camPos;
    vec3 rd = normalize(filmPos - camPos);
    fragColor = vec4(0,0,0,1);

    rtpm h = intersect(ro, rd,0);    
    if(h.dh) {
        fragColor += vec4(h.col,0,0,1);
    }
     h = intersect(ro, rd,1);    
    if(h.dh) {
        fragColor += vec4(0,h.col,0,1);
    }
     h = intersect(ro, rd,2);    
    if(h.dh) {
        fragColor += vec4(0,0,h.col,1);
    }

}
