#define PI 3.14159265359

mat2 getRotationMatrix2(float theta) {
    return mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
}

int murmurHash32(int h) {
    h ^= h >> 16;
    h *= 0x85ebca6b;
    h ^= h >> 13;
    h *= 0xc2b2ae35;
    h ^= h >> 16;
    return h;
}


vec2 randomNormalizedVec2(ivec2 seed) {
    int combinedSeed = int(seed.x) * 73856093 + int(seed.y) * 19349663;
    int hash = murmurHash32(combinedSeed);
    int randInt = hash & 0xFFFFFFFF;
    float randFloat = float(randInt) / float(0xFFFF);
    float randRads = randFloat * 2.0 * PI;
    return vec2(1, 0) * getRotationMatrix2(randRads);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 shiftedFragCoord = fragCoord + vec2(1000000, 1000000.0);
    float sum = 0.0;
    int startingPower = 1;
    int layerCount = 9;
    for(int layer = 0; layer < layerCount; layer++)
    {
        int power = startingPower + layer;
        float scale = pow(2.0, float(power));
        ivec2 iFragCoord = ivec2(shiftedFragCoord / scale);
        vec2 randomVector = randomNormalizedVec2(iFragCoord);
        float value = dot(randomVector, vec2(1, 0) * getRotationMatrix2(iTime*2.0)) / (pow(2.0, float(layer)) * 2.0);
        if(layer == layerCount - 1) {
            value *= 2.0;
        }
        sum += value;
    }
    fragColor = vec4(sum, sum, sum, 1.0);

}
