float speed = 7.;

float sminkio(float s) {
    return floor(1./(1. + sin(speed * iTime * s * .03)) * 500.)/4000.;
}


vec3 index(float omega) {
    float n = floor(4.*(1. + sin(iTime * omega)));
    //return vec3(mod(n,2.),mod(n/2.,2.),mod(n/4.,2.));
    return vec3(mod(n,2.),mod(n/2.,2.),mod(n/4.,2.));
}

//vec2 v = M * vec2(1.,2.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 col = vec3(
        sminkio(2.*log(1.-uv.x*uv.y+ iTime * 0.03 ) * 10.),
        0.4 * sminkio(log((3.-sin(iTime * 0.9))*(1.5+uv.y) + (3.+sin(iTime * 0.6))* (1.5+uv.x)) * 1.),
        sminkio(sin(iTime * 0.2) * exp(.5*(1.-uv.x)*sin(iTime + 1.5*uv.y))- (1.-sin(iTime * 0.1)) * .01 * exp(uv.x) * 1.)
        //sminkio(exp(uv.x*sin(iTime + 10.*uv.y))-exp(uv.y/iTime) * 1.)
    );
    const float k = 0.05;
    mat3 M = mat3(-.23 *cos(iTime) * k,-.543 * sin(iTime) * k,.65* sin(iTime) * k,-.76*cos(iTime) * k,.654 * cos(iTime) * k,-.22*cos(iTime) * k,.54* sin(iTime) * k,.125 * k,.653 * k);
    // Output to screen

    //float a = (1.+sin()

    vec3 i = index(.3);

    col += i.x * M * col * 5.;
    col -= i.y * .2*sin(2.*iTime)*sin(0.01*iTime) * vec3(col.r/(col.g+col.b),col.g/(col.b+col.r),col.b/(col.r+col.g));
    col *= .5*(4.+3.*sin(iTime));

    fragColor = vec4(col,1.0);
}
