vec3 palette(float t, vec3 a, vec3 b, vec3 c, vec3 d) {
 return a + b*cos( 6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;

 vec2 uv0 = uv;

 vec3 finalColor = vec3(0.0);


 for (float i=.0;i<2.; i++) {

     uv = fract(uv * 1.5)-.5;

     float d = length(uv)*exp(-length(uv0));

     vec3 grad1 = vec3(0.5, 0.5, 0.5);
     vec3 grad2 = vec3(0.5, 0.5, 0.5);
     vec3 grad3 = vec3(1.0, 1.0, 1.000);
     vec3 grad4 = vec3(0.263, 0.416, 0.557);

     vec3 col = palette(length(uv0)+i*+iTime*.4,grad1,grad2,grad3,grad4);

     d = sin(d*8. + iTime)/8.;
     d = abs(d);

     d = pow(0.01 / d,1.2);


     finalColor += col *d;
 }


 fragColor = vec4(finalColor,1);
}
