// Chinese Flag by Rik Riesmeijer (CC0)
float star(vec2 v) {
    mat2 z = mat2(v.x, -v.y, v.y, v.x);
    z = z * z * z * z * z - mat2(1);
    return smoothstep(2.5, 1.5, determinant(z));
}

void flagCN(out vec4 c, vec2 v) {
    v = (v - 0.5 * iResolution.xy) * 30.0 / min(iResolution.x, iResolution.y);
    c = mix(vec4(0.93, 0.11, 0.145, 1), vec4(1, 1, 0, 1),
            star( v.yx / 3.0 + vec2(-2.5,   5)) +
            star( v.yx / 3.0 + vec2(-2.5,   5)) +
            star( v.xy       + vec2( 8.0, - 2)) +
            star( v.xy       + vec2( 8.0, -12)) +
            star( v.yx       + vec2(-5.0,   5)) +
            star(-v.xy       + vec2(-5.0,   9)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    flagCN(fragColor, fragCoord);
}
