// cosine based palette, 4 vec3 params
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.283185*(c*t+d) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float deg = //iMouse.x/iResolution.x;
    .35;
    float sep = //iMouse.y/iResolution.y;
    .4;
    float val = pow(length(uv-vec2(sep,0.)),deg)+pow(length(uv+vec2(sep,0.)),deg);

    vec3 col = palette(val*5.,vec3(.5),vec3(.5),vec3(1.),vec3(0.,.1,.2));
    fragColor = vec4(pow(col,vec3(.454545)),1.0);
}
