void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    uv -= 0.5;

    uv += 0.02 * sin(vec2(uv.y * 10.0, uv.x * 10.0));

    float noise = 0.1 * sin(uv.x * 50.0) * sin(uv.y * 50.0);
    uv += noise;

    float raindrop = smoothstep(0.005, 0.008, sin(uv.y * 100.0));

    vec3 bgColor = vec3(0.0, 0.1, 0.2);
    vec3 rainColor = vec3(0.5, 0.6, 0.7);
    vec3 finalColor = mix(bgColor, rainColor, raindrop);

    fragColor = vec4(finalColor, 1.0);
}
