const vec4 c1 = vec4(0.5, 0.5, 1.0, 1.0);
const vec4 c2 = vec4(0.3, 0.3, 0.5, 1.0);
const float size = 20.0;
const float speed = 3000.0;
const float edge = 0.05;
const vec2 edgeVec = vec2(0.005, -0.0025);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float t = iTime * speed;
    vec2 animPos = fragCoord - edgeVec * t;
    vec2 d = fract(animPos / size) - 0.5;

    float s = dot(fragCoord, edgeVec);
    float h = smoothstep(0.5 - edge, 0.5 + edge, length(d) + s);

    fragColor = c1 * h + (1.0 - h) * c2;
}
