void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2. - iResolution.xy)/iResolution.y;
    float d = length(uv);
    d = sin(d*8.+iTime)/8.;
    d = abs(d);
    // Time varying pixel color
    d = step(0.1,d);
    // Output to screen
    fragColor = vec4(d,d,d,1.0);
}
