float reamap(float In, float InMin,float InMax, float OutMin,float OutMax){
    float Out = OutMin + (In - InMin) * (OutMax - OutMin) / (InMax - InMin);    
    return Out;
}
vec3 palette(float t)
{
    vec3 a = vec3(0.5,0.5,1.0);
    vec3 b = vec3(0.5,0.5,1.0);
    vec3 c = vec3(0.5,0.5,1.);
    vec3 d = vec3(0.2,0.4,0.6);

    return a + b*cos(6.28318*(c*t + d));

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv0 =fragCoord/iResolution.xy;
    vec2 uv =(2.0*fragCoord - iResolution.xy)/iResolution.y;

    vec3 col = palette(length(uv0) + iTime*2.);

    float circle = length(uv)-0.7;
    float smoothCircle = smoothstep(10./iResolution.y,0.,circle);
    float outedge =clamp ( 0.03/clamp(circle,0.,1.),0.,1.) - smoothCircle;

    float noise1 = texture(iChannel0,uv*2./iResolution.y -iTime*0.005).x;
    float noise2 = texture(iChannel0,uv*2./iResolution.y + iTime*0.005).x;
    float noise = noise1 + noise2;

    float reamapUV = reamap(uv.y,0.,1.,-0.5,1.);
    float wave = reamapUV + noise;
    float edge = smoothstep(0.,50./iResolution.y,wave) ;

    vec4 rColor = smoothCircle *edge* vec4(1.5,0.5,0.,1.);
    vec4 lColor = smoothCircle *(1.-edge)* vec4(0.,0.5,1.5,1.);

    vec4 s1 = rColor + lColor ;
    vec4 s2 = outedge * vec4(col,1.);


    fragColor = s2 + s1 ;


}
