#define RES iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - RES.xy * 0.5)/RES.y*-50.0;

    uv+=uv*0.1/exp(uv.x+iTime*0.001);

    // Time varying pixel color
    vec3 col = vec3(abs(uv.x)-round(abs(uv.x))>0.001) + vec3(abs(uv.x)-round(abs(uv.y))>0.001);

    // Output to screen
    fragColor = vec4(exp(uv.x*0.01), log(col.y), abs(log(uv.x)),1.0);
}
