float intensive = 1.5; // intensive = [0.0, 1.5] 

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = texture(iChannel0, uv);
    vec4 bufferA = texture(iChannel1, uv);
    //color = mix(color, bufferA, 0.5);
    color += max(bufferA * intensive, vec4(0.0));
    // Output to screen
    fragColor = color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 posMouse = iMouse.xy;

    posMouse /= iResolution.xy;

    vec4 light = vec4(1.0, 1.0, 0.8, 1.0);

    float dist = distance(uv, posMouse) * 1.1;

    vec4 color = vec4(0.0);
    color += light * (1.0 - dist);

    // Output to screen
    fragColor = color;
}
