void mainImage(out vec4 o, vec2 u) {
    float s = 6.;
    vec2 r = iResolution.xy;
    u = (s - 4.) * (u + u - r) / r.y;

    //if(false)
    u = vec2(
           atan(u.x, u.y) - iTime / 4.,
           log(length(u)) - .5
       ) * 3./.314 ;

    float t = fract(iTime / 2.),
          i = floor(u.x),
          y = floor(u.y + t),
          T = 1. - floor(sin(.628 * i) * 1.2 + 4.) + s,
          B =      floor(sin(.314 * i) * 1.3 + 4.) - s;


    u.y = fract(u.y + t);

    if(B <= y && y <= T )
        u.y = u.y + y - mix(T, B, t);


    o = vec4(
               + .5 
               - length(
                     mod(u, vec2(1, T - B + 1.)) - .5
                 )

             ) / fwidth(length(u)) +.5;
}
