#define pi acos(-1.)
#define t iTime * .4

float cell(vec2 p){
    return max(
               length(max(abs(p) - vec2(.12), 0.)),
               -(length(p) - .12)
           );
}

float grid(vec2 p) {

    float idy,
          w = 3.14 / 12.,
          l = length(p),
          h = 1.;

    p = vec2(
            atan(p.x, p.y),
            log(l)
        );

    idy = floor(p.y / w);

    if(idy > -5.)
        p.x += tanh(fract(t * idy) * 3.)  * 3.14 / 12.;

    p = mod(p, w) - w / 2.;

    return l * cell(p);
}



void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy; 
         u = vec2(u + u - r) / r.y;

    o = vec4(smoothstep(.0, 3. / r.y, grid(u)));
}
