const float borderThickness = 10.0;
const float radius = 20.0;
const vec4 borderColor = vec4(1.0, 1.0, 0.0, 1.0);
const vec2 widowSize = vec2(300.0, 200.0);

const float halfBorderThickness = borderThickness / 2.0;
const vec2 halfWindowSize = widowSize / 2.0;

vec3 getColor(vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    return 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
}

float RectSDF(vec2 p, vec2 b, float r) {
    vec2 d = abs(p) - b + vec2(r);
	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - r;   
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 centeredPos = (fragCoord - iResolution.xy / 2.0);
    float dist = RectSDF(centeredPos, halfWindowSize + halfBorderThickness, radius - halfBorderThickness);
    dist = abs(dist) - halfBorderThickness;
    float blendAmount = smoothstep(-1.0, 1.0, dist);
    fragColor = mix(vec4(getColor(fragCoord), 1.0), vec4(0.0), blendAmount);
}
