#define S(a, b, t) smoothstep(a, b, t)


float N(float t){
    return fract(sin(t*3456.)*6547.);
}

vec4 N14(float t) {
    return fract(sin(t*vec4(4324., 2347., 8647., 5253.)) * vec4(4354., 6472., 8858., 7485.));
}

struct ray {
    vec3 o, d;
};

ray Getray(vec2 uv, vec3 camPos, vec3 lp) {
    ray a;
    a.o = camPos;
    float zoom = 1.0;

    vec3 f = normalize(lp - camPos);
    vec3 r = cross(vec3(.0, 1.0, .0), f);
    vec3 u = cross(f, r);
    vec3 c = a.o + f * zoom;
    vec3 i = c + uv.x * r + uv.y * u;
    a.d = normalize(i - camPos);

    return a;
}

vec3 ClosestPoint(ray r, vec3 p) {
    return r.o + dot(p-r.o, r.d)*r.d;
}

float DistRay(ray r, vec3 p) {
    return length(p-ClosestPoint(r, p));
}

float bokeh(ray r, vec3 p, float size, float blur) {
    float d = DistRay(r, p);
    size *= length(p);
    float c = S(size, size*(1.-blur), d);
    c *= mix(0.6, 1., S(0.8*size, size, d));
    return c;
}

vec3 StreetLight(ray r, float t){
    float side = step(r.d.x, 0.);
    r.d.x = abs(r.d.x);
    float s = .1;
    float m = 0.;

    for(float i = 0.; i<1.; i+=s) {
        float ti = fract(i+t+side*s*0.5);
        vec3 p = vec3(2., 2., 100.-ti*100.);
        m += bokeh(r, p, 0.05, 0.1)*ti*ti;
    }

    return vec3(1., .7, .3)*m;
}

vec3 EnvLight(ray r, float t){
    float side = step(r.d.x, 0.);
    r.d.x = abs(r.d.x);
    float s = .1;
    vec3 c = vec3(0.);

    for(float i = 0.; i<1.; i+=s) {
        float ti = fract(i+t+side*s*0.5);

        vec4 n = N14(i+side*100.);
        float fade = ti*ti*ti;

        float occlusion = sin(ti*6.28*10.)*n.x*.5 + .5;
        fade = occlusion;
        float x = mix(2.5, 10., n.x);
        float y = mix(.1, 1.5, n.y);

        vec3 p = vec3(x, y, 50.-ti*50.);
        vec3 col = n.wzy;
        c += bokeh(r, p, 0.05, 0.1)*fade*col*.5;
    }

    return c;
}

vec3 HeadLight(ray r, float t){
    t*= 2.;
    float s = 1./30.;
    float m = 0.;
    float w1 = 0.25;
    float w2 = w1*1.2;

    for(float i = 0.; i<1.; i+=s) {
        float n = N(i);
        if (n > .1) continue;

        float ti = fract(i+t);
        float z = 100.-ti*100.;
        float fade = ti*ti*ti*ti*ti;
        float focus = S(.9, 1., ti);
        float size = mix(.05, .03, focus);
        m += bokeh(r, vec3(-1.+w1/2., .15, z), size, 0.1)*fade;
        m += bokeh(r, vec3(-1.-w1/2., .15, z), size, 0.1)*fade;

        m += bokeh(r, vec3(-1.+w2/2., .15, z), size, 0.1)*fade;
        m += bokeh(r, vec3(-1.-w2/2., .15, z), size, 0.1)*fade;

        float ref = 0.;
        ref += bokeh(r, vec3(-1.+w2/2., -.15, z), size*3., 1.)*fade;
        ref += bokeh(r, vec3(-1.-w2/2., -.15, z), size*3., 1.)*fade;

        m += ref*focus;
    }

    return vec3(.9, .9, 1.)*m;
}

vec3 TailLight(ray r, float t){
    t *= .25;
    float s = 1./15.;
    float m = 0.;
    float w1 = 0.25;
    float w2 = w1*1.2;

    for(float i = 0.; i<1.; i+=s) {
        float n = N(i);
        if (n > .5) continue;

        float lane = step(.25, n);
        float ti = fract(i+t);
        float z = 100.-ti*100.;
        float fade = ti*ti*ti*ti*ti;
        float focus = S(.9, 1., ti);
        float size = mix(.05, .03, focus);

        float laneShift = S(1., .96, ti);
        float x = 1.5 - lane * laneShift;

        float blink = step(0., sin(ti*1000.))*7.*lane*step(.96, ti);
        m += bokeh(r, vec3(x+w1/2., .15, z), size, 0.1)*fade;
        m += bokeh(r, vec3(x-w1/2., .15, z), size, 0.1)*fade;

        m += bokeh(r, vec3(x+w2/2., .15, z), size, 0.1)*fade*(1.+blink);
        m += bokeh(r, vec3(x-w2/2., .15, z), size, 0.1)*fade;

        float ref = 0.;
        ref += bokeh(r, vec3(x+w2/2., -.15, z), size*3., 1.)*fade*(1.+blink*0.1);
        ref += bokeh(r, vec3(x-w2/2., -.15, z), size*3., 1.)*fade;

        m += ref*focus;
    }

    return vec3(1., .1, .03)*m;
}

vec2 Rain(vec2 uv, float t){
    t *= 40.;
    vec2 a = vec2(3., 1.);
    vec2 st = uv*a;
    st.y += t * .22;
    vec2 id = floor(st);
    float n = fract(sin(id.x*456.2)*546.34);
    st.y += n;
    uv.y += n;
    id = floor(st);
    st = fract(st)-.5;

    t += fract(sin(id.x*34.54+id.y*2284.54)*45.98)*6.283;
    float y = -sin(t+sin(t+sin(t)*.5))*.43;
    vec2 pl = vec2(0., y);

    vec2 o1 = (st-pl)/a;
    float d = length(o1);
    float m1 = S(.07, .0, d);

    vec2 o2 = (fract(uv*a.x*vec2(1., 2.))-.5)/vec2(1., 2.);
    d = length(o2);
    float m2 = S(.3*(.5-st.y), .0, d)*S(-.1, .1, st.y - pl.y);
    //if (st.x > 0.46 || st.y > .49) m1 = 1.;
    return vec2(m1*o1*30. + m2*o2*10.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 m = iMouse.xy / iResolution.xy;
    float t = iTime*.05+m.x;

    vec3 camPos = vec3(.5, .2, .0);
    vec3 lookat = vec3(.5, .2, 1.);

    vec2 rainDistortion = Rain(uv*5., t)*.5;
    rainDistortion += Rain(uv*7., t)*5.;

    uv.x += sin(uv.x *70.)*.005;
    uv.y += sin(uv.y *170.)*.004;

    ray r = Getray(uv-rainDistortion*.1, camPos, lookat);

    vec3 col = StreetLight(r, t);
    col += HeadLight(r, t);
    col += TailLight(r, t);
    col += EnvLight(r, t);

    col += (r.d.y + .25)*vec3(.2, .1, .5);

    //col=vec3(Rain(uv.xy, t), 0.);
    // Output to screen
    fragColor = vec4(col, 1.);
}
