void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(.45);

    uv.x += iTime*0.05;
    uv.y += iTime*0.125;

    uv *= 13.0;

    vec2 GRID = fract(uv) - 0.5;
    vec2 ID   = floor(uv);

    GRID.x = texture(iChannel0, 3.1*ID).r < 0.5 ? -GRID.x : GRID.x;
    float signedness = 0.0<(GRID.x+GRID.y+0.0001)
        ? -1.0
        :  1.0;
    col += smoothstep(0.01, -0.01, abs(signedness*0.5+GRID.x+GRID.y)-0.45)*vec3(.6,.5,.2);

    fragColor = vec4(col,1.0);
}
