void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;



    float yAxis = uv.y * 0.1;


    float itd = iTimeDelta;

    float itdLine = step(itd, yAxis);


    float avg = 1.0/(float(iFrame)/iTime);

    float avgLine = step(avg, yAxis);

    // Output to screen
    fragColor = vec4(uv.y, itdLine, avgLine, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;


