float rand(vec2 co) {
    float timeSeed = iTime * 1000.0;
    return fract(sin(dot(co , vec2(12.9898, 78.233))) * 43758.5453);
}


vec3 rainbow(float t) {
    return 0.5 + 0.5 * cos(t + vec3(0.0, 2.0, 4.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = fragCoord.xy / iResolution.xy;


    int numSpritesX = 10;
    int numSpritesY = 10;


    float frame = floor(mod(iTime * 10.0, 6.0));


    float squareWidthUV = 40.0 / 256.0;


    float squareSize = 0.1;  


    vec4 baseColor = vec4(0.0, 0.0, 0.0, 0.0);


    float spacingX = 1.0 / float(numSpritesX);
    float spacingY = 1.0 / float(numSpritesY);


    for (int y = 0; y < numSpritesY; y++) {
        for (int x = 0; x < numSpritesX; x++) {

            vec2 spritePos = vec2(float(x) * spacingX, float(y) * spacingY);


            vec2 randomOffset = vec2(rand(vec2(float(x), float(y))), rand(vec2(float(x) + 1.0, float(y) + 1.0)));


            spritePos += randomOffset * spacingX;


            spritePos.x += sin(iTime * 1.0 + float(x)) * 0.18;
            spritePos.y += sin(iTime * 1.0 + float(y)) * 0.18;


            vec2 offset = (spritePos - uv);


            if (abs(offset.x) < squareSize && abs(offset.y) < squareSize) {

                vec2 spriteUV = (uv - spritePos) / squareSize + vec2(0.5, 0.5);


                spriteUV.y = 1.0 - spriteUV.y;


                spriteUV = (spriteUV - vec2(0.5)) * (0.05 / squareSize) + vec2(0.5);


                spriteUV.x = (spriteUV.x + frame) * squareWidthUV;


                vec4 spriteTexture = texture(iChannel0, spriteUV);


                float rainbowTime = iTime + length(spritePos);  
                vec3 rainbowColor = rainbow(rainbowTime * 0.5); 


                vec3 trailColor = rainbowColor * 0.6; 
                baseColor.rgb = mix(baseColor.rgb, trailColor, 0.5); 


                float alpha = spriteTexture.a;
                baseColor.rgb = mix(baseColor.rgb, spriteTexture.rgb, alpha);
                baseColor.a = mix(baseColor.a, 1.0, alpha);
            }
        }
    }

    fragColor = baseColor;
}
