uvec3 pcg3d(uvec3 v) {
    v = v * 1664525u + 1013904223u;
    v.x += v.y * v.z; v.y += v.z * v.x; v.z += v.x * v.y;
    v ^= v >> 16u;
    v.x += v.y * v.z; v.y += v.z * v.x; v.z += v.x * v.y;
    return v;
}

vec3 pcg33(vec3 p)
{
    uvec3 r = pcg3d(floatBitsToUint(p));
    return vec3(r) / float(0xffffffffu);
}

vec3 tree(vec2 clip, float pxsz, vec2 pos, float size, vec3 cin, vec3 col, float r, float r2)
{
    clip -= pos;
    clip /= size;
    pxsz /= size;
    float d = .5 + abs(10. * clip.x)
        - .5 * sin(20. * clip.y * (r + .5)
        + (r2 - .5) * 2. * iTime)
        + (clip.y / 2. - .5) * 8.;
    float intree = smoothstep(0., -40. * pxsz, d);
    intree *= smoothstep(0., .1, clip.y + .9);
    return mix(cin, col, intree);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 clip = 2. * fragCoord / iResolution.xy - vec2(1.);
    clip.x *= iResolution.x / iResolution.y;
    float pxsz = 2. / iResolution.y;

    vec3 fog = vec3(.8, .9, 1.);
    vec3 base = vec3(.4, .2, .05);
    float d = clip.y * .5 + .5;
    vec3 col = mix(base, fog, d);
    float num = 50.;
    for(float i = 0.; i < num; i += 1.)
    {
        vec3 rnd = pcg33(vec3(5., 17., i));
        vec3 rndc = pcg33(vec3(1., 17., i));
        col = tree(
            clip,
            pxsz,
            vec2((rnd.x * 2. - 1.) * 5. * (i / 2. / num + .3), 1. - i / (num / 1.7)),
            .7 * (i / 2. /num + .3),
            col,
            mix((rndc * .3 + vec3(.15, .5, .25)), fog, .8 - i / num),
            rnd.y,
            rnd.z
        );
    }

    vec3 noise = pcg33(vec3(fragCoord.xy, iFrame));

    fragColor = vec4(col + .2 * (noise -.5), 1.) ;
}
