float v = 0.;

bool circle(vec2 st, vec2 center, float radius)
{
    return distance(st, center) < radius;
}


float heartRadius(float theta)
{
    return 2. - 2.*sin(theta) + sqrt(abs(cos(theta)))*sin(theta)/(1.4 + sin(theta));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2. - iResolution.xy)
        		/ min(iResolution.x, iResolution.y);
    v  = texture( iChannel0, vec2(1/510,0.25) ).x;

    float red  = texture( iChannel0, vec2(1/510,0.25) ).x;
    float grn  = texture( iChannel0, vec2(0.5,0.5) ).x;
    float blu  = texture( iChannel0, vec2(0.75,0.5) ).x;

    vec4 col = vec4(1.);
    vec2 originalPos = (2.0 * fragCoord - iResolution.xy)/iResolution.yy;
    vec2 pos = originalPos;
    pos.y -= 0.55;        	

    float theta = atan(pos.y, pos.x);
    float r = heartRadius(theta);
    vec2 boxCenter = vec2(0.0, -0.55 * v);
    float boxRadius = 0.15 * v;

    vec4 heartColor = vec4(red, grn, blu,1.0);
    vec4 bgColor = vec4(0.0,0.0,0.0,1.0);
    if (circle(uv, boxCenter, boxRadius))
    {
        col = vec4(red,grn,blu,1.);
    }else{
        col = mix(bgColor, heartColor,
                    smoothstep(0.0, length(pos) * 0.5, r *.5*v* 0.25 ));
    }

    fragColor = col;
}
