float log2_approx(float x) {
  return uintBitsToFloat(floatBitsToUint(256.f) | (floatBitsToUint(x) >> 8u)) - 383.f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor.rgb = vec3(log2(uv.x*8.))/3.;
    if(uv.y>0.5){
        fragColor.rgb = vec3(log2_approx(uv.x*8.))/3.;
    }
}
