void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2(0.0, 0.0) is bottom left coordinate, vec2(1.0, 1.0) is top right
    vec2 texcoord = fragCoord/iResolution.xy;

    //Applies an offset to the sampled pixel coordinates
    vec2 coordShift = vec2(-0.1, -0.1);

    //fetch texture color
    vec3 col = texture(iChannel0, texcoord + coordShift).rgb;

    vec3 colMult = vec3(1.0, 0.9, 0.3); //Color to multiply
    vec3 colAdd  = vec3(0.0, 0.1, 0.7); //Color to add

    float colDiv  = 1.0 + texcoord.x; //divides the color more further right

    col = col * colMult + colAdd;//Blending

    col = col / colDiv;//More blending

    // Output to screen
    fragColor = vec4(col,1.0);
}
