vec2 cmul(vec2 a, vec2 b) {
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

vec2 cdiv(vec2 a, vec2 b) {
    return cmul(a, vec2(b.x, -b.y)) / dot(b, b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 pos = fragCoord.xy / iResolution.xy * 2. - 1.;
    pos.x *= iResolution.x / iResolution.y;

    float th = iTime * 0.3;

    //vec2 a = vec2(cos(th), sin(th)) * 4.;//vec2(0.);
    vec2 a = vec2(th, -th);
    vec2 b = vec2(1., 1.);
    //b = vec2(cos(iTime), sin(iTime));
    vec2 c = vec2(1., -1);
    vec2 d = vec2(0.);
    vec2 cpos = cdiv(cmul(a, pos) + b, cmul(c, pos) + d);

    vec3 col;

    col = texture(iChannel0, fract(cpos + 0.5)).rgb;

    col = pow(col, vec3(0.3, 0.9, 3.)).bgr;
    col= mix(vec3(1.000,0.000,0.584), vec3(0.000,1.000,0.800), pow(col, vec3(0.2)));

    col = mix(vec3(0.863,0.996,0.969), col, 1. - exp(-length(pos) * 2.));

    fragColor = vec4(col,1.0);
}
