void mainImage(out vec4 o, vec2 q)
{
    vec2 r = iResolution.xy;
    float f = fract(sin(q.y * 99.) * 77.), 
        p = mod(f * r.x / 5. * (55. + iTime), r.x);
    o = vec4(sqrt(f * (1. - abs(q.x - p))));
}
