bool drawRectangle(vec2 fragCoord, vec2 pos, vec2 size, vec2 resolution) {
    vec2 posNorm = vec2(pos.x, resolution.y - pos.y - size.y);
    return (fragCoord.x >= posNorm.x && fragCoord.x <= posNorm.x + size.x &&
            fragCoord.y >= posNorm.y && fragCoord.y <= posNorm.y + size.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 rectPos = vec2(100.0, 100.0);
    vec2 rectSize = vec2(200.0, 100.0);

    if (drawRectangle(fragCoord, rectPos, rectSize, iResolution.xy)) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
