//----------------------------------------------------
// PolarMod_windmill.glsl                   2024-12-24
// https://www.shadertoy.com/view/4XdcDH
// interactive windmill using  function PolarMod(p,n);
// move mouse to change sectors
// tags: 2d, texture, windmill, PolarMod, rotate
//----------------------------------------------------

const float PI = acos(-1.0);
const float TAU = 2.0 * PI;

mat2 rotate(float a) 
{
  float c = cos(a), s = sin(a);
  return mat2(c,s,-s,c);
}

// get polar mod rotated position
vec2 PolarMod(vec2 p, float n) 
{
  n = TAU / n;
  float a = atan(p.x, p.y) + n;
  return p * rotate(floor(-a / n)*n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 R = iResolution.xy;
  vec2 uv = (2.0 * fragCoord - R) / R.y;
  vec2 mp = (2.0 * iMouse.xy) / R.y;
  float n = 5.0;
  if (mp.x >= 0.1) 
    n = 2. + 4.*mp.x;                   // real
//  n = 2. + floor(4.*mp.x);            // integer
  uv = PolarMod(uv,n) + 0.2*sin(iTime);
  fragColor = texture(iChannel0, uv);   // Output to screen
}
