const vec3 white = vec3(1.0);
const vec3 black = vec3(0.0);
const vec3 blue = vec3(0.34, 0.23, 0.78);
const vec3 purple = vec3(0.65, 0.15, 0.65);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 col;

    if(uv.y > 0.5) 
    {
        uv.y = (uv.y - 0.5) * 2.0;

        if(uv.y > 0.5) 
        {
            uv.y = (uv.y - 0.5) * 2.0;
            col = mix(blue, purple, uv.y); 
        }
        else 
        {
            uv.y = uv.y * 2.0;
            col = mix(white, blue, uv.y);
        }
    }
    else 
    {
        uv.y = uv.y * 2.0;
        col = mix(purple, black, uv.y);
    }

    fragColor = vec4(col, 1.0);  
}
