#define PI 3.14159265359

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = (iTime+94.0+148.0) * 0.954;
    vec3 color = vec3(0.0);
    float intensity = 0.0;
    float s = 3.14;

    int adjustedFrame = max(iFrame%300 + 100, 0);
    int NUM_PARTICLES = min(200, adjustedFrame / 100 + 100);
    s = s +(sin(sin(iTime*0.1)))*1.2;
    for (int i = 0; i < NUM_PARTICLES; i++) {
        float id = float(i) / 89.0;
        float particleTime = id * 50.0 * sin(time / 30.0 * s);


        float theta = fract(id + particleTime * 0.0009) * 20.0 * s;
        float phi = fract(id * 1.618 + particleTime * 0.015) * s;

        vec3 spherePos = vec3(
            sin(phi) * cos(theta),
            sin(phi) * sin(theta),
            cos(phi)
        );


        float angle = time * 0.28;
        mat3 rotation = mat3(
            cos(angle), 0.0, -sin(angle),
            0.0,        1.0,  0.0,
            sin(angle), 0.0,  cos(angle)
        );
        spherePos = rotation * spherePos;


        vec2 screenPos = spherePos.xy / (spherePos.z + 1.5);


        float dist = length(uv - screenPos);
        float glow = exp(-dist * 17.0);

        // Усиление интенсивности
        intensity += glow * (0.19 + abs(sin(iTime / 10.0)));
        color += glow * vec3(
            0.4 + 0.6 * sin(id * 5.28318), 
            0.3 + 0.4 * sin(time * 2.0), 
            1.0
        );
    }

    // Окончательный цвет с учетом интенсивности
    fragColor = vec4(color * intensity, 1.0-1.5/intensity);
}
