#define S(a, b, t) smoothstep(a, b ,t )  
#define cl(x) clamp(x, 0., 1.)

float remap01(float a, float b, float t){

    return cl((t-a) / (b-a));

}

float remap(float a, float b, float c, float d, float t){

    return cl((t-a) / (b-a)) * (d-c) + c;

}

vec2 within(vec2 uv, vec4 rect) {

    return (uv-rect.xy) / (rect.zw-rect.xy);

}


vec4 Head (vec2 uv) {

    vec4 col = vec4(.8, .0, .1, 1.0);

    float d = length(uv);

    col.a = S (.5, .49, d);

    float edgeShade = remap01 (.36, .5, d);

    edgeShade *= edgeShade;

    col.rgb *= 1.-edgeShade*.5;

    col.rgb = mix(col.rgb, vec3(.3, .0, .1), S(.47, .49, d) );

    float highlight = S(.45, .42, d);

    highlight *= remap (-.41, .1, .5, 0., uv.y );


    col.rgb = mix(col.rgb, vec3(1.), highlight);

    float darklight = S(.45, .42, d);

    darklight = remap (-.45, .1, .6, 0., -uv.y );

    col.rgb = mix(col.rgb, vec3(0.), darklight);

    return col;
}

vec4 Eye (vec2 uv, float side, vec2 m, float smile){

    uv-=.5;

    uv.y -= .15;

    uv.x *= side;     

    float d = length(uv);


    vec4 irisCol = vec4 (.6, .1, .4, 1.);

    vec4 col = mix (vec4(1.), irisCol, S(.1,.7,d)*1.5);   //iris

    col.a = S(.5,.48,d);


    col.rgb *= 1. - S(.45, .5, d) *.5 * cl(-uv.y-uv.x * side); //slight eye shadow

    d = length(uv-m*.7);

    col.rgb = mix(col.rgb, vec3(.8, 0., 0.), S(.25, .19, d));  //outer pupil


    vec3 pupil = vec3 (.25, .0, .0);

    irisCol.rgb *= 1. - S(.25, .05, d);  // inner pupil

    float pupilsize = mix(.25, .17, smile);

    col.rgb = mix(col.rgb, pupil, S(pupilsize, pupilsize * .85, d));  // inner pupil

    d = length(uv-m*.78);


    col.rgb = mix(col.rgb, irisCol.rgb, S(.17, .14, d));  // inner pupil



    float darklight = S(.45, .42, d);

    darklight = remap (-.35, .05, .6, 0., -uv.y );

    col.rgb = mix(col.rgb, vec3(0.), darklight);


    return col;
}


vec4 Mouth (vec2 uv, float smile){

    uv-=.5;
    uv.y -= uv.x * uv.x * 5. ;

    uv.x *= mix(2., .9, smile);

    uv.y *=3.;

    vec4 col = vec4(.2, .0, .05, 1.);

    float d =length(uv);

    col.a =S(.5, .45, d);

    float td = length (uv-vec2(0., .4));

    //col.rgb = mix(col.rgb, vec3(.3, .1, .0), S(.4, .37, td));

    td = length(uv+vec2(0., .4));

    col.rgb = mix(col.rgb, vec3(.1,.0,.0), S(.5, .2, td));

    return col;
}



vec4 Smiley (vec2 uv, vec2 m, float smile){



    vec4 mouth = Mouth(within(uv, vec4(-.4, -.45, .4, -.19)), smile);

    float side = sign(uv.x);

    uv.x = abs (uv.x);


    vec4 col = vec4(.0);



    vec4 head = Head(uv);
    vec4 eye = Eye(within(uv, vec4(.06, -.10, .35, .28)), side, m, smile);


    col = mix(col, head, head.a);
    col = mix(col, eye, eye.a);
    col = mix(col, mouth, mouth.a);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv-=.5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 m = iMouse.xy / iResolution.xy;

    m -= .5;

    if(m.x<-.49 && m.y<-.49) {			
    	float s = sin(iTime*.5);
        float c = cos(iTime*.38);

        m = vec2(s, c)*.4;
    }

    if(length(m) > .707) m *= 0.;	

    float d = dot(uv, uv);

    uv -= m*cl(.23-d);


    float smile = cos(iTime) *.5 + .5;



    // Output to screen
    fragColor = Smiley(uv,m, smile);
}
