// Shadertoy uniforms
uniform vec3 iGlobalResolution;
uniform float iGlobalTime;
uniform vec4 iGlobalMouse;

// Complex number multiplication
vec2 complex_mul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

// Complex number addition
vec2 complex_add(vec2 a, vec2 b) {
    return vec2(a.x + b.x, a.y + b.y);
}

// Mandelbrot set fractal algorithm
float mandelbrot(vec2 c) {
    vec2 z = vec2(0.0, 0.0);
    float n = 0.0;
    for (int i = 0; i < 256; i++) {
        if (length(z) > 2.0) break;
        z = complex_add(complex_mul(z, z), c);
        n += 1.0;
    }
    if (n == 256.0) return 0.0;
    return n + 1.0 - log2(log2(length(z)));
}

// Function to map pixel coordinates to the complex plane
vec2 map_to_complex(vec2 uv, float zoom, vec2 offset) {
    return (uv - 0.5) * zoom + offset;
}

// Function to map iteration count to color
vec3 color_map(float n) {
    float t = fract(n * 0.04 + iTime * 0.1);
    return 0.5 + 0.5 * cos(6.2831 * (vec3(0.0, 0.33, 0.67) + t));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Animate the zoom and offset to create a zooming effect
    float zoom = 2.0 + 0.5 * sin(iTime * 0.1);
    vec2 offset = vec2(0.5 * sin(iTime * 0.2), 0.5 * cos(iTime * 0.2));

    // Map the pixel coordinates to the complex plane
    vec2 c = map_to_complex(uv, zoom, offset);

    // Calculate the Mandelbrot set value
    float m = mandelbrot(c);

    // Map the iteration count to a color
    vec3 color = color_map(m);

    // Output the final color
    fragColor = vec4(color, 1.0);
}
