#define PI (3.14159265358979)
#define TWOPI (2.0*PI)


// https://gist.github.com/983/e170a24ae8eba2cd174f

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
//    c.x /= TWOPI;

    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


// License: CC0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    float min_dimension = min(iResolution.x, iResolution.y);
    vec2 uv = fragCoord/min_dimension;
    uv = uv*2.0 - vec2(iResolution.x, iResolution.y)/min_dimension;

	// parameters
    float r = length(uv);
	float theta = atan(uv.y, uv.x);
    float t = iTime;
    float thickness = 0.5;

    // Spiral
    float theta1 = theta + t*TWOPI;
    float parts1 = log(r)*TWOPI - theta1;
    float modpart1 = mod(parts1, TWOPI);
    float divpart1 = parts1 - modpart1;

    float col1 = (theta1 + divpart1)/12.0;

    float col2 = (sin(PI/2.0 + (log(r)*TWOPI - theta1))+1.0)/2.0;
    col2 = abs(1.0-col2);
    col2 = sqrt(sqrt(col2))+0.25;

    // Output to screen
    fragColor = vec4(col2*hsv2rgb(vec3(col1,1.0,1.0)),1.0);
}
