void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 5.0;

    float wave = sin(20.0 * length(uv) - time * 2.0);
    float spiral = sin(20.0 * atan(uv.y, uv.x) - time * 5.0);

    float pulse = sin(iTime * 5.0) * 0.5 + 0.5;

    vec3 rainbowColor = 0.5 + 0.5 * cos(time + vec3(0.0, 2.0, 4.0) + uv.xyx * 2.0);

    vec3 baseColor = vec3(0.5 + 0.5 * cos(wave + spiral + time));
    vec3 invertedColor = vec3(1.0) - baseColor;
    vec3 color = mix(baseColor, invertedColor, 1.0 - pulse);

    color *= rainbowColor;

    fragColor = vec4(color, 1.0);
}
