void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    float lipestok = sin(uv.x * 10.0 + iTime) * 0.5 + sin(uv.y * 10.0 - iTime) * 0.5;
    float spin = atan(uv.y, uv.x) + iTime;
    float rad = length(uv) * 1.5;
    float pattern = mix(lipestok, rad, 0.7) + sin(spin * 10.0) * 0.3;

    vec3 color;
    if (pattern < 0.2) {
        color = vec3(1.0, 1.0, 0.0);
    } 
    else if (pattern < 0.5) {
        color = vec3(0.0, 0.7, 0.0);
    } 
    else {
        color = vec3(0.5, 0.0, 1.0);
    }


    fragColor = vec4(color, 1.0);
}
