void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (fragCoord.y / iResolution.y >= 0.66) fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    if (fragCoord.y / iResolution.y <= 0.66) fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    if (fragCoord.y / iResolution.y <= 0.33) fragColor = vec4(0.0, 1.0, 0.0, 1.0);
}
