void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy / iResolution.xy;

    float pixelPosition = st.x;

    float pixelWidth = 10.0 / iResolution.x;

    float offset = mod(iTime * 0.10, 1.0);

    float distance = abs(pixelPosition - offset);

    float alpha = smoothstep(0.0, pixelWidth, distance);

    vec3 black = vec3(0.0, 1.0, 0.0);

    vec3 finalColor = mix(black, vec3(0.0, 0.0, 0.0), alpha);

    fragColor = vec4(finalColor, 1.0);
}
