void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture( iChannel0, uv ).rgb * 2.;

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define I iResolution

void mainImage( out vec4 U, in vec2 V )
{
    vec2 u = ( V * 2. - I.xy ) / I.y;

    vec3 c;
    float p;


    p = length( u + vec2( cos( iTime ), cos( iTime ) * .5 ) ) - .1;

    c = vec3( .5, .5, 1.9 ) * step( p, 0. );

    U = vec4(c,1.0);

    U.rgb = .02 * U.rgb + texture( iChannel0, V / I.xy ).rgb * .98; 
}
