void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.-1.;
    uv.x *= iResolution.x/iResolution.y;
    float c;
    vec4 pa = vec4(0.2,0.2,0.5,1.0);

    for(int i = 0; i<1;i++){
        uv = fract(uv*1.1)*2.0-1.0;
        float l = length(uv);
        l = sin(l*3.1415926535*2.0);
        c = 0.5/(l+1.0);
        c = mod(c*1.8+iTime*0.5,1.0);
    }
    pa.xyz = pa.xyz*c;
    fragColor = vec4(pa);
}
