void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.x=uv.x*iResolution.x/iResolution.y;
    uv.xy*=4.0;
    float speed=10.0;
    uv.xy*=sin(iTime*3.0);
    uv.x*=cos(iTime*speed);
    uv.x+=tan(iTime)-1.0/tan(iTime);
    uv.y+=1.0/tan(iTime)-tan(iTime);
    uv.y*=cos(iTime*speed);
    float col=step(abs(uv.x)+abs(uv.y),0.25);

    float coll;
    if(abs(uv.x)<=0.175 && abs(uv.y)<=0.175){coll=1.0;}
    else coll=0.0;


    // Output to screen
    fragColor = vec4(vec3(col+coll),1.0);}
