void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 1.2;
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y * zoom;
    vec2  m = (iMouse.xy * 2. - iResolution.xy) / iResolution.y * zoom;

    float dentPos = sin(iTime) * 2.0;
    float dent = exp(uv.y - dentPos) * 0.5;
    float denom = 0.1;
    float nom = uv.y + dent / (uv.x * uv.x * 4. + 0.5);
    float nLine = nom / denom;
    float repY = mod(nom, denom);
    float lineStop = 8.;
    float lineCol = 1. - smoothstep(0.02, 0., repY) * smoothstep(lineStop + 0.1, lineStop, nLine);
    float borderedLines = lineCol * smoothstep(1., 0.99, length(uv));
    vec3 bgCol = vec3(1.) * (1. - smoothstep(1., 0.99, length(uv)));
    vec3 col = vec3(borderedLines) + bgCol;


    fragColor = vec4(col,1.0);
}
