// loopless by @fabrice

void mainImage(out vec4 o, vec2 u) {
    float s = 9.;
    vec2 r = iResolution.xy;
    u = s * (u + u - r) / r.y;

    float t = fract(iTime / 2.),
          i = floor(u.x),
          y = floor(u.y + t),
          T = 1. - floor(sin(.3 * i) * 1.5 + 5.) + s,
          B =      floor(sin(.7 * i) * 1.4 + 4.) - s;

    t -= y > T || y < B 
                  ? y 
                  : T - (T - B) * t;

    o = r.yyyy / 25. 
            * (
                .5 
                - length(
                      vec2(
                          u.y + t,
                          fract(u)
                      ) - .5
                  )
              );        
}


