vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

mat2 rot2D( float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    vec2 q = uv;
    vec3 finalColor = vec3(0.0);

    float ncircsbase = 6.0;

    for (float j = 0.0; j < 7.0; ++j) {
        vec2 off = vec2(0.0, 0.25*(j+1.0));
        for (float i = 0.0; i < ncircsbase*(j+1.0); ++i) {
            float s = (mod(floor(j), 2.0) == 0.0) ? 1.0 : -1.0;
            vec2 off2 = off * rot2D(s*(6.28138*i/(ncircsbase*(j+1.0))+(iTime/((1.0 - exp(1.0*length(off)))))));
            float r = length(q - off2);
            float d0 = length(off2) + 0.1*length(q);
            vec3 col = palette(r*.3+d0);

            float d = length(off);
            d = sin(d*8.)/8.;
            d = abs(d);
            d = smoothstep(0.,.1,d);
            float cr = 0.108 + 0.0189*sin(4.0*iTime*d);
            finalColor += (1.0 - step(cr, r)) * col;
        }
    }

    finalColor = 1.0 - finalColor;
    fragColor = vec4(finalColor,1.0);
}
