#define rot(x) mat2(cos(x+vec4(0,11,33,0)))
#define H(h,id)  (  cos( h/2.*cos(iTime/2.) + .01*id + vec3(1,2,3)  )*.8  + .5 )
#define H2(h)  (  cos(  h/2. + vec3(3,2,1)   )*.7 + .2 )
#define M(c)  log(1.+c)
#define R iResolution

void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .5*R.y, R.y))*400.;

    float sc,dotp,totdist=0., tt=iTime/5., t=iTime/2.;

    for (float i=0.; i<80.; i++) {

        vec4 p = vec4( rd*totdist);

        p.xyz += vec3(4.*sin(t),0,-63); 

        p.yzw = p.xyz;

        p.xw *= rot(tt);
        p.xy *= rot(1.57);

        vec3  id = round(p.xyw*20.);

        sc = 1.; 

        vec4 w = p;

        for (float j=0.; j<5.; j++) {

            p = abs(p)*.5;

            dotp = max(1./dot(w,w),.78);
            sc *= dotp; 

            p = p * dotp - .3; 

            w = .7*log(1.+log(1.+log(1.+p)*p)*p) - .4*p;


        }

        float dist = abs( length(p.xz)-.01)/sc ; 
        float stepsize = dist/50. ;     
        totdist += stepsize;                  //move the distance along rd

        //accumulate color, fading with distance and iteration count
        c +=
            .02*  H(M(sc),id) * exp(-i*i*stepsize*stepsize*1e3);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
