#define PI 3.14
#define TAU 6.28
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 _uv = uv;
    float div = 0.01f;
    float col = 1.+floor(uv.x / div);

    // Time varying pixel color
    uv.y = fract(uv.y + (abs(col - 1./(2.*div)) * .05 * iTime * .1));
    vec3 color = 0.5 + 0.5 * cos(iTime * (sin(col)*0.5 + 0.6) + vec3(0., uv.y, 1.-uv.y) * TAU * 3.);

    color = pow(color, vec3(1./2.2));

    float g = _uv.y - 0.5;
    float vign = 1.-(g*g)*6.;
    color *= vign;

    // Output to screen
    fragColor = vec4(color,1.0);
}
