void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float s = .1;

    float speed = 4.1;
    float b = .037;
    vec4 noise = texture(iChannel0, uv*s);

    vec2 uv2 = vec2(uv.x + cos(iTime*speed+uv.x*speed)*b*noise.r*noise.a, uv.y + sin(iTime*speed+uv.y*speed)*b*noise.g*noise.b);


    vec4 c = texture(iChannel0, uv2 *s);
    fragColor = vec4(c.x*.8,c.y*.8,c.y* .5,c.x) * smoothstep(.0, .9, c.x) + texture(iChannel1,uv) * .2;
}
