void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;

    vec2 a = uv*9.0;

    float A = sin(fract(a.x+sin(uv.y*(1.0+sin(iTime))*2.0+iTime*3.0)/4.0)*3.14);
    float B = sin(fract(0.25+a.x+cos(uv.y*(1.0+cos(iTime))*3.0+iTime*2.0)/4.0)*3.14);


    vec3 col = vec3(A, B, A+B);


    fragColor = vec4(col, 1.0);
}
