void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv *= 4.0;

    uv += vec2(sin(uv * 5.0 + (cos(iTime * 0.3))) * 0.45);
    uv += vec2(cos(uv.x * 2.5 + (iTime * 0.15)) * 0.5);

    float mask = texture(iChannel0, uv).x;
    float mask2 = smoothstep(0.5, 0.6, mask);

    vec3 black = vec3(0.0, 0.0, 0.0);
    vec3 white = vec3(1.0, 1.0, 1.0);

    vec3 result = vec3(mix(black, white, mask2));

    fragColor = vec4(result, 1.0);
    //fragColor = vec4(fract(uv), 0.0, 1.0);
}
