vec2 sineWave(vec2 uv, float frequency, float amplitude) {
                uv.x += amplitude * sin(uv.y * frequency + iTime);
                return uv;
            }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float bass = texture(iChannel0, vec2(0.,0.)).x; 
    vec2 uvwarp = sineWave(uv,0., 0.01);  
    uvwarp.y = uvwarp.y > 0.5?1.-uvwarp.y:uvwarp.y; 
vec3 col = texture(iChannel0, uvwarp).xyz*1./texture(iChannel1, sin(iTime)*uv+(iTime)+bass).xyz; 
    // Output to screen
    fragColor = vec4(col,1.0);
}
