#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)
//#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-.57,.02,-.84),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)/2.;
    p=R3D(iTime);

    for(j=0.;j++<4.;)
        p=1./abs(abs(p*fract(iTime*.08))-.02)-.03,
        d=max(max(p.y,p.z),p.x),
        d-=dot(p,p)/2e4;

    e+=d;
}o+=vec4(exp(p*p*q),1)*3./i;
}
