// NO WAR with IRAN
// https://www.youtube.com/watch?v=KOgLUB3MBes

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define iBeatNrg    0.0
#define iBeatDet    0.01
#define iBeat       iTime

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants

#define hoff (0.734+sin(PI*.5*.05*iTime))
#define cycle 0.9 * ((iBeatNrg>0.01)?(iBeatNrg*(1.+cos(3.141592*2.*iBeat)*.4)):.0)

#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))
#define sunColor   HSV2RGB(vec3(hoff+0.0, 0.9, 0.0005))
#define topColor   HSV2RGB(vec3(hoff+0.0, 0.9, 0.0002*(1.+iBeatNrg*iBeatDet)))
#define glowColor0 HSV2RGB(vec3(hoff+0.0, 0.9, 0.0001*cycle))
#define glowColor2 HSV2RGB(vec3(hoff+0.3, 0.95, 0.001))
#define diffColor  HSV2RGB(vec3(hoff+0.0, 0.9, .25*cycle))

#define ROTY(a)               \
  mat3(                       \
    +cos(a) , 0.0 , +sin(a) \
  , 0.0     , 1.0 , 0.0     \
  , -sin(a) , 0.0 , +cos(a) \
  )

#define ROTZ(a)               \
  mat3(                       \
    +cos(a) , +sin(a) , 0.0   \
  , -sin(a) , +cos(a) , 0.0   \
  , 0.0     , 0.0     , 1.0   \
  )

#define ROTX(a)               \
  mat3(                       \
    1.0 , 0.0     , 0.0       \
  , 0.0 , +cos(a) , +sin(a)   \
  , 0.0 , -sin(a) , +cos(a)   \
  )

const mat3 roty       = ROTY(radians(10.0));
const vec3 sunDir     = normalize(vec3(0.0, -0.01, 1.0))*roty;
const vec3 lightPos   = vec3(0.0, -60.0, -200.0)*roty;


// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(vec3 t) {
  return mix(1.055*pow(t, vec3(1./2.4)) - 0.055, 12.92*t, step(t, vec3(0.0031308)));
}

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

float rayPlane(vec3 ro, vec3 rd, vec4 dim) {
  return -(dot(ro,dim.xyz)+dim.w)/dot(rd,dim.xyz);
}
vec3 hueShift(vec3 col, float shift) {
    vec3 m = vec3(cos(shift), -sin(shift) * .57735, 0);
    m = vec3(m.xy, -m.y) + (1. - m.x) * .33333;
    return mat3(m, m.zxy, m.yzx) * col;
}
vec2 planeCoord(vec3 p, vec3 c, vec3 up, vec4 dim) {
  vec3 d = p - c;
  vec3 xx = (cross(up,dim.xyz));
  vec3 yy = (cross(xx,dim.xyz));
  return vec2(dot(d,xx), dot(d,yy));
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float triIso(vec2 p, vec2 q) {
  p.x = abs(p.x);
  vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
  vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
  float s = -sign( q.y );
  vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                vec2( dot(b,b), s*(p.y-q.y)  ));
  return -sqrt(d.x)*sign(d.y);
}

float lineSeg(vec2 p, vec2 a, vec2 b) {
    float thickness = 1.0/100.0;

    vec2 pa = p - a;
    vec2 ba = b - a;

    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );

    float idk = length(pa - ba*h);

    return smoothstep(0.0, thickness, idk);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/intersectors/
vec2 rayBox(vec3 ro, vec3 rd, vec3 boxSize, out vec3 outNormal)  {
  vec3 m = 1.0/rd; // can precompute if traversing a set of aligned boxes
  vec3 n = m*ro;   // can precompute if traversing a set of aligned boxes
  vec3 k = abs(m)*boxSize;
  vec3 t1 = -n - k;
  vec3 t2 = -n + k;
  float tN = max( max( t1.x, t1.y ), t1.z );
  float tF = min( min( t2.x, t2.y ), t2.z );
  if( tN>tF || tF<0.0) return vec2(-1.0); // no intersection
  outNormal = (tN>0.0) ? step(vec3(tN),t1)  : // ro ouside the box
                         step(t2,vec3(tF))  ;  // ro inside the box
  outNormal *= -sign(rd);
  return vec2( tN, tF );
}

vec3 sky(vec3 ro, vec3 rd) {
  vec3 col = vec3(0.0);
  col += sunColor/(1.0+0.00001 - dot(sunDir, rd));
  float hd = max(abs(rd.y+0.15), 0.00066);
  col += 100.0*glowColor0/sqrt(hd);
  col += glowColor2/(hd);
  return col;
}

// License: Unknown, author: Claude Brezinski, found: https://mathr.co.uk/blog/2017-09-06_approximating_hyperbolic_tangent.html
float tanh_approx(float x) {
  //  Found this somewhere on the interwebs
  //  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

vec3 glow(vec3 ro, vec3 rd) {
  vec3 bn;
  vec3 bro = ro;
  bro.y += -1000.0+70.0;
  vec2 bi = rayBox(bro, rd, vec3(90.0, 1000.0, 90.0), bn);
  float lightDist = distance(lightPos, ro);
  vec3 lightDir   = normalize(lightPos-ro);
  float g3        = 1.0+0.00001 - dot(lightDir, rd);
  vec3 col = vec3(0.0);
  col += 8.0*glowColor0/(g3);
  vec3 rrd = rd*transpose(roty)*ROTX(0.027);
  if (bi != vec2(-1.0)) {
    float bdi = tanh_approx(0.00125*(bi.y-bi.x));
    col += 1000.0*glowColor0*(bdi/max(rrd.y, 0.005));
  }

  float sx = abs(rrd.x);
  col += 20.0*glowColor0/(abs(mix(20.0*rrd.y*rrd.y, abs(rrd.y), tanh_approx(4.0*sx)))+2.0*sx*sx*sx+0.0001);

  return col;
}

float DrawLine(vec2 p, vec3 A, vec3 B)    
{
    //A.zy *= v; // pitch
    //A.zx *= h; // yaw
   // B.zy *= v; // pitch
   // B.zx *= h; // yaw

    vec2 a = A.xy, 
         b = B.xy - a;
    p -= a;
    float hx = clamp(dot(p, b) / dot(b, b), 0., 1.);
    return length(p - b*hx) + .01*mix(A.z, B.z, hx);     
} 


vec3 side(vec3 col, vec3 ro, vec3 rd, vec3 nrd, float t, float nt, vec4 dim, vec3 c) {
  vec3 n = dim.xyz;

  vec3 p = ro + rd*t;
  vec3 np = ro + nrd*t;
  vec2 u = rd.xy;
  vec3 r = reflect(rd, n);
  vec3 ldiff = p - lightPos;
  vec3 ld = normalize(ldiff);
  vec3 rcol0 = sky(p, r);
  float dcol = max(dot(ld, n), 0.0);
  dcol *= dcol;
  float aa = distance(p, np);
  vec2 pp = planeCoord(p, c, vec3(0.0, 1.0, 0.0), dim);
  vec2 p0 = pp;
  vec2 p1 = pp;
  const vec2 tri =vec2(485, sqrt(3.0)*356.0);
  float d0 = triIso(p0, tri);
  float d1 = triIso(p1, .1*tri);
  float d = d0;

  vec3 tx = vec3(0);
   vec3 k = 0.8*cos(tx.x*8.9)*cos(tx.x*8.9)*vec3(0.15, 0.25, 0.55) ;

  vec3 bcol = col;
  float hf = smoothstep(-600.0, -400.0, p.y);
  vec3 pcol = 3.0*diffColor*dcol;  
  pcol += rcol0;
  pcol = mix(clamp(col, 0.0, 0.1), pcol, hf); 
  col  = mix(col, pcol, smoothstep(aa, 0.0, d));
  col += topColor/max(0.00005*(d1-1.), 0.000025)*hf;
 // col += .01 * k/abs(DrawLine(u, p, np));
  return col;
}

vec3 pyramid(vec2 u, vec3 col, vec3 ro, vec3 rd, vec3 nrd) {

   mat3 rrr = ROTY(0.1*iTime-iMouse.x/iResolution.x*4.);

  const mat3 rotx = ROTX(radians(-51.8));
  const mat3 rr0  = rotx;
  const mat3 rr1  = rr0*ROTY( PI/2.);
  const mat3 rr2  = rr0*ROTY(-PI/2.);
  const mat3 rr3  = rr0*ROTY( PI   );

   vec3 n0   = normalize(vec3(0.0, 0.0, 1.0))*rr0*rrr;
  const vec3 c0 = vec3(0.0);
   vec4 dim0 = vec4(n0, -dot(c0, n0));

   vec3 n1  = normalize(vec3(0.0, 0.0, 1.0))*rr1*rrr;
   vec4 dim1 = vec4(n1, -dot(c0, n1));

   vec3 n2  = normalize(vec3(0.0, 0.0, 1.0))*rr2*rrr;
   vec4 dim2 = vec4(n2, -dot(c0, n2));

   vec3 n3  = normalize(vec3(0.0, 0.0, 1.0))*rr3*rrr;
   vec4 dim3 = vec4(n3, -dot(c0, n3));

  float t0  = rayPlane(ro, rd , dim0);
  float nt0 = rayPlane(ro, nrd, dim0);

  float t1  = rayPlane(ro, rd , dim1);
  float nt1 = rayPlane(ro, nrd, dim1);

  float t2  = rayPlane(ro, rd , dim2);
  float nt2 = rayPlane(ro, nrd, dim2);

  float t3  = rayPlane(ro, rd , dim3);
  float nt3 = rayPlane(ro, nrd, dim3);

  if (t0 > 0.0 && nt0 > 0.0) {
    col = side(col, ro, rd, nrd, t0, nt0, dim0, c0);
  }
  if (t1 > 0.0 && nt1 > 0.0) {
    col = side(col, ro, rd, nrd, t1, nt1, dim1, c0);
  }
  if (t2 > 0.0 && nt2 > 0.0) {
    col = side(col, ro, rd, nrd, t2, nt2, dim2, c0);
  }
  if (t3 > 0.0 && nt3 > 0.0) {
    col = side(col, ro, rd, nrd, t3, nt3, dim3, c0);
  }

  return col;
}

vec3 pyramid_wire(vec2 u, vec3 ro, vec3 rd, vec3 nrd, vec2 m) {

   mat3 rrr = ROTY(0.1*iTime-iMouse.x/iResolution.x*4.-0.94);

  const mat3 rotx = ROTX(radians(-51.8*.555));
  const mat3 rr0  = rotx;
  const mat3 rr1  = rotx*ROTY( PI/2.);
  const mat3 rr2  = rotx*ROTY(-PI/2.);
  const mat3 rr3  = rotx*ROTY( PI   );

   vec3 n0   = normalize(vec3(0.0, 0.0, 1.0))*rr0*rrr;
  const vec3 c0 = vec3(0.0);
   vec4 dim0 = vec4(n0, -dot(c0, n0));

   vec3 n1  = normalize(vec3(0.0, 0.0, 1.0))*rr1*rrr;
   vec4 dim1 = vec4(n1, -dot(c0, n1));

   vec3 n2  = normalize(vec3(0.0, 0.0, 1.0))*rr2*rrr;
   vec4 dim2 = vec4(n2, -dot(c0, n2));

   vec3 n3  = normalize(vec3(0.0, 0.0, 1.0))*rr3*rrr;
   vec4 dim3 = vec4(n3, -dot(c0, n3));

  float t0  = rayPlane(ro, rd , dim0);
  float nt0 = rayPlane(ro, nrd, dim0);

  float t1  = rayPlane(ro, rd , dim1);
  float nt1 = rayPlane(ro, nrd, dim1);

  float t2  = rayPlane(ro, rd , dim2);
  float nt2 = rayPlane(ro, nrd, dim2);

  float t3  = rayPlane(ro, rd , dim3);
  float nt3 = rayPlane(ro, nrd, dim3);

      vec2 R = iResolution.xy;

    vec4 tx = vec4(0); //vec4(texture(iChannel0, u.xy));
    vec3 k = hueShift(.9*vec3(0.15, 0.25, 0.55),(PI*.5*.05*iTime)) ; //0.8*cos(tx.x*8.9)*cos(tx.x*8.9)*vec3(0.15, 0.25, 0.55) ;
     float s = 1.9;


 // float DrawLine(vec2 p, mat2 v, mat2 h, vec3 A, vec3 B)   

    vec3 c = vec3(0), p2; // RGB3

    c += k/abs( DrawLine( u, c0, s * n0 ) + .015 );
    c += k/abs( DrawLine( u, c0, s * n3 ) + .015 );
    c += k/abs( DrawLine( u, c0, s * n2 ) + .015 );
    c += k/abs( DrawLine( u, c0, s * n1 ) + .015 );

    c += k/abs( DrawLine( u, s * n0, s * n1 ) + .015 );
    c += k/abs( DrawLine( u, s * n1, s * n2 ) + .015 );
    c += k/abs( DrawLine( u, s * n2, s * n3 ) + .015 );
    c += k/abs( DrawLine( u, s * n3, s * n0 ) + .015 );

    return .05*c; //col+cc*0.05;
}

vec3 color(vec2 u, vec3 ww, vec3 uu, vec3 vv, vec3 ro, vec2 p) {
  const float rdd = 3.0;
  const float mm = 4.0;
  vec2 np = p + 4.0/RESOLUTION.y;

  vec3 rd   = normalize(-p.x*uu + p.y*vv + rdd*ww);
  vec3 nrd  = normalize(-np.x*uu + np.y*vv + rdd*ww);
  vec3 glowCol = glow(ro, rd);

  vec3 col = sky(ro, rd); 
  col = pyramid(np, col, ro, rd, nrd);
  col += glowCol;  
  return col;
}

vec3 color2(vec2 u, vec3 ww, vec3 uu, vec3 vv, vec3 ro, vec2 p, vec2 m) {
  const float rdd = 3.0;
  const float mm = 4.0;
  vec2 np = p + 4.0/RESOLUTION.y;

  vec3 rd   = normalize(-p.x*uu + p.y*vv + rdd*ww);
  vec3 nrd  = normalize(-np.x*uu + np.y*vv + rdd*ww);
  vec3 glowCol = glow(ro, rd);

  return pyramid_wire(np, ro, rd, nrd, m);
}

vec3 effect(vec2 u, vec2 p, vec2 pp) {
  const vec3 ro = vec3(0.0, 0.0, -2700.0)*roty;
  const vec3 la = vec3(0.0, 0.0, 0.0);
  const vec3 up = vec3(0.0,1.0,0.0);
  vec3 ww = normalize(la - ro);
  vec3 uu = normalize(cross(up, ww));
  vec3 vv = (cross(ww, uu));
  vec3 col = color(u, ww, uu, vv, ro, p);
  col = aces_approx(col);
  col = sRGB(col);
  return col;
}


vec3 effect2(vec2 u, vec2 p, vec2 pp, vec2 m) {
  const vec3 ro = vec3(0.0, 0.0, -2700.0)*roty;
  const vec3 la = vec3(0.0, 0.0, 0.0);
  const vec3 up = vec3(0.0,1.0,0.0);
  vec3 ww = normalize(la - ro);
  vec3 uu = normalize(cross(up, ww));
  vec3 vv = (cross(ww, uu));

  vec3 col = color2(u, ww, uu, vv, ro, p, m);
  return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoords )
{
    vec2 R = iResolution.xy,
         m = (iMouse.xy*2.-R)/R.y;

     vec2 q = fragCoords/RESOLUTION.xy;

    float t = iTime/60.;   
    float tx = .03+.5*pow(1.8*vec4(texture(iChannel0, 
        vec2((fragCoords.y/iResolution.y)*30.,0.2))).r,3.);

    if (iMouse.z < 1.) // not clicking 
        m = vec2(sin(t*PI)*2., sin(t*PI*2.)); 

  vec2 pq = -1. + 2. * q;
  vec2 pp = pq;
  pq.x *= R.x/R.y;
    fragColor = vec4(effect(q, pq, pp) + tx * effect2(q, pq, pp, m) , 1);
}
